          "general_readiness_evidence": "<evidence from transcription for quality/score>"
        },
        "sku_plan": {
          "evaluation_criteria": "Did he plan in advance which SKUs he would try to sell today?",
          "sku_plan_quality": "Partially Done",
          "sku_plan_score": 2,
          "sku_plan_evidence": "<evidence from transcription for quality/score>"
        },
        "personalization": {
          "evaluation_criteria": "Did he write down the shop names and plan the route?",
          "personalization_quality": "Completely Done",
          "personalization_score": 2,
          "personalization_evidence": "<evidence from transcription for quality/score>"
        },
        "written_explicit_plan": {
          "evaluation_criteria": "Were the objective and tasks written down or explicitly stated before entry?",
          "written_explicit_plan_quality": "Completely Done",
          "written_explicit_plan_score": 2,
          "written_explicit_plan_evidence": "<evidence from transcription for quality/score>"
        },
        "core_preparation_subtotal": "general_readiness_score + sku_plan_score + personalization_score + written_explicit_plan_score",
        "core_preparation_percentage": "(core_preparation_subtotal ÷ 8) × 100"
